package com.example.schedulemanager;import android.content.Context;import android.content.SharedPreferences;import java.util.List;public class AutoEmailStrings {    public static SharedPreferences EmailStringSharedPreferences;    final private static String EMAIL_STRING_DB = "Email_String_DB";    public static List<String> autoEmailStrings;    public static List<String> getAutoEmailStrings() {        return autoEmailStrings;    }    public static void setAutoEmailStrings(List<String> autoEmailStrings) {        AutoEmailStrings.autoEmailStrings = autoEmailStrings;    }    public static void getFilled(){        autoEmailStrings.add("patelvatsalb21@gmail.com");    }    public static void addStringsForEmail(String s, Context context){        autoEmailStrings.add(s);        EmailStringSharedPreferences = context.getSharedPreferences("Email_String.db", Context.MODE_PRIVATE);        SharedPreferences.Editor editor =  EmailStringSharedPreferences.edit();        editor.putString(EMAIL_STRING_DB,s);        editor.commit();    }}