package com.example.schedulemanager.email;import java.util.Calendar;public class Email {		public long id;	public String From;	public String To;	public String Subject;	public String Message;	public Calendar cal;	public Boolean Scheduled;	public Email() {	}	public Email(long id, String from, String to, String subject, String message, Calendar cal, Boolean scheduled) {		this.id = id;		From = from;		To = to;		Subject = subject;		Message = message;		this.cal = cal;		Scheduled = scheduled;	}	public Email(String from, String to, String subject, String message, Calendar cal, Boolean scheduled) {		From = from;		To = to;		Subject = subject;		Message = message;		this.cal = cal;		Scheduled = scheduled;	}	public String getFrom() {		return From;	}	public void setFrom(String from) {		From = from;	}	public long getId() {		return id;	}	public void setId(long id) {		this.id = id;	}	public Boolean getScheduled() {		return Scheduled;	}	public void setScheduled(Boolean scheduled) {		Scheduled = scheduled;	}	public String getTo() {		return To;	}	public void setTo(String to) {		To = to;	}	public String getSubject() {		return Subject;	}	public void setSubject(String subject) {		Subject = subject;	}	public String getMessage() {		return Message;	}	public void setMessage(String message) {		Message = message;	}	public Calendar getCal() {		return cal;	}	public void setCal(Calendar cal) {		this.cal = cal;	}	public static Boolean isEmailAheadOfTime(Calendar calGiven){		Calendar cal = Calendar.getInstance();		return calGiven.compareTo(cal) > 0;	}	public static Email findEmailByID(long id){		for (Email e : UtilsArray_Email.mail){			if (e.id == id){				return e;			}		}		return null;	}}